package com.example.zhou.mypowerbee.sdk.netlog;//网络配置文件public class NetConfigFile {	public static String ENCODING = "utf-8";	public static ServerIpEnum serverIpEnum = ServerIpEnum.POWERBEE;//默认服务器地址	public static ServerIpEnum serverUpdateIpEnum = ServerIpEnum.POWERBEE;//选择服务器地址	public static String SERVER_TCP_IP =  serverIpEnum.getIp();//"122.114.57.65";//	public static String SERVER_HTTP_IP =  SERVER_TCP_IP;//"115.2";//"122.114.57.65";//	public static int SERVER_TCP_PORT = serverIpEnum.getPort();	public static int SERVER_HTTP_PORT =SERVER_TCP_PORT+1;	public static int CONCENTROTOR_PORT = SERVER_HTTP_PORT;	public static String SERVER_NAME = serverIpEnum.getServerName();	//更新维护的服务器地址	public static String SERVER_HTTP_IP_UPDATE =  serverUpdateIpEnum.getIp();	public static int SERVER_HTTP_PORT_UPDATE =serverUpdateIpEnum.getPort()+1;	//关闭设备是否需要提醒 0为不提醒，1为提醒	public static int POWER_OFF_IS_NEED_REMIND = 1;		public static void setServerIpEnum(ServerIpEnum serverIpEnum1){		serverIpEnum = serverIpEnum1;		SERVER_TCP_IP =  serverIpEnum.getIp();//"122.114.57.65";// 		SERVER_HTTP_IP =  SERVER_TCP_IP;//"115.2";//"122.114.57.65";//		SERVER_TCP_PORT = serverIpEnum.getPort();		SERVER_HTTP_PORT =SERVER_TCP_PORT+1;		CONCENTROTOR_PORT = SERVER_HTTP_PORT;		SERVER_NAME = serverIpEnum.getServerName();	}	}